import React, { Component } from 'react';
import Header from '../header'
import './addUser.css'
import { Input, Select , InputNumber,  Cascader, Button } from 'antd';


const InputGroup = Input.Group;
const { Option } = Select;
const option1 = [
  {
    value: 'Active',
    label: 'Active',
  },
  {
    value: 'Inactive',
    label: 'Inactive',
  },
];

const option2 = [
  {
    value: 'Content Writer',
    label: 'Content Writer',
  },
  {
    value: 'Admin',
    label: 'Admin',
  },
];

class AddUser extends Component {

  state = {
      userName: '',
      emailId: '',
      password: '',
      mobileNumber: '',
      role: '',
      status: ''
  }

  AddUser = (e) => {
  e.preventDefault()

     fetch("http://192.168.29.236:5000/user", {
         method: "POST",
         headers: {
           Accept: "application/json",
           "Content-Type": "application/json"
         },
         body: JSON.stringify({
         "_id":this.state.Id, "name": this.state.CustomerName, "ph": this.state.PhoneNumber, "address": this.state.Address
         })
       }).then(res => {
           if (res.ok) {
             return res.json();
           } else {
             throw Error(res.statusText);
           }
         })
         .then(json =>  console.log(json))
     .catch( err => console.log(err))
}



  render(){
    return <>
    <Header />
    <div className='container-fluid f1 row justify-content-center'>
     <div className='shadow col-md-5 f2'>


    <div style={{ marginBottom: 16 }}>
      <Input addonBefore="User Name"  defaultValue="" onChange={ e => this.setState({ userName: e.target.value })} value={this.state.userName} />
    </div>

    <div style={{ marginBottom: 16 }}>
      <Input addonBefore="Email ID"  defaultValue="" onChange={ e => this.setState({ password: e.target.value })} value={this.state.password}/>
    </div>


    <div style={{ marginBottom: 16 }}>
      <Input addonBefore="Password"  defaultValue="" onChange={ e => this.setState({ password: e.target.value })} value={this.state.password}/>
    </div>


    <div style={{ marginBottom: 16 }}>
      <Input addonBefore="Mobile Number"  defaultValue="" onChange={ e => this.setState({ mobileNumber: e.target.value })} value={this.state.mobileNumber}/>
    </div>

   <div style={{  marginBottom: 16 }}>
    <InputGroup compact className='text-center'>
            <Cascader style={{ width: '100%' }} options={option2} placeholder="Select Role"   onChange={ e => this.setState({ role: e.target.role })}/>
    </InputGroup>
   </div>

<div style={{  marginBottom: 16 }}>
    <InputGroup compact className='text-center '>
            <Cascader style={{ width: '30%' }} options={option1} placeholder="Select Status"  onChange={ e => this.setState({ status: e.target.status })}/>
    </InputGroup>
   </div>

    <Button block className='f3' onClick={this.AddUser}>Create</Button>
    </div>
    </div>
    </>
  }
}

export default AddUser;
